# MEthod 1def read_large_file(file_path):    with open(file_path, 'r') as f:        for line in f:            yield line.strip()# Example usagefile_path = 'large_file.txt'for line in read_large_file(file_path):    # Process each line here    print(line)# MEthod 2: Using chunkingdef read_large_file_chunks(file_path, chunk_size=1024):    with open(file_path, 'r') as f:        while True:            chunk = f.read(chunk_size)            if not chunk:                break            yield chunk# Example usagefile_path = 'large_file.txt'for chunk in read_large_file_chunks(file_path):    # Process each chunk here    print(chunk)